// src/components/TransactionTable.js
import React, { useState, useEffect } from 'react';
import TransactionStatistics from './TransactionStatistics';
import TransactionBarChart from './TransactionBarChart';

const TransactionTable = () => {
    const [transactions, setTransactions] = useState([]);
    const [selectedMonth, setSelectedMonth] = useState('March');
    const [searchTerm, setSearchTerm] = useState('');
    const [page, setPage] = useState(1);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchTransactions();
    }, [selectedMonth, searchTerm, page]);

    const fetchTransactions = async () => {
        setLoading(true);
        setError(null);
        try {
            const response = await fetch(`https://664c7d2a35bbda10988094cc.mockapi.io/task1/roxiler?month=${selectedMonth}&page=${page}&search=${searchTerm}`);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setTransactions(data);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
        setPage(1); // Reset to first page on new search
    };

    const handleMonthChange = (event) => {
        setSelectedMonth(event.target.value);
        setPage(1); // Reset to first page on month change
    };

    const handlePreviousPage = () => {
        setPage((prevPage) => Math.max(prevPage, 1));
    };

    const handleNextPage = () => {
        setPage((prevPage) => prevPage + 1);
    };

    return (
        <div>
            <h2>Transaction Dashboard</h2>
            <div>
                <input
                    type="text"
                    placeholder="Search transaction"
                    value={searchTerm}
                    onChange={handleSearch}
                />
                <select value={selectedMonth} onChange={handleMonthChange}>
                    {['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'].map(month => (
                        <option key={month} value={month}>{month}</option>
                    ))}
                </select>
            </div>
            <TransactionStatistics selectedMonth={selectedMonth} />
            <TransactionBarChart selectedMonth={selectedMonth} />
            {loading ? (
                <p>Loading...</p>
            ) : error ? (
                <p>Error: {error}</p>
            ) : (
                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Title</th>
                            <th>Description</th>
                            <th>Price</th>
                            <th>Category</th>
                            <th>Sold</th>
                            <th>Image</th>
                        </tr>
                    </thead>
                    <tbody>
                        {transactions.map(transaction => (
                            <tr key={transaction.id}>
                                <td>{transaction.id}</td>
                                <td>{transaction.title}</td>
                                <td>{transaction.description}</td>
                                <td>{transaction.price}</td>
                                <td>{transaction.category}</td>
                                <td>{transaction.sold}</td>
                                <td><img src={transaction.image} alt={transaction.title} width="50" /></td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
            <div>
                <button onClick={handlePreviousPage} disabled={page === 1}>
                    Previous
                </button>
                <span>Page No: {page}</span>
                <button onClick={handleNextPage}>
                    Next
                </button>
            </div>
        </div>
    );
};

export default TransactionTable;
