import React, { useState, useEffect } from 'react';

const ChartComponent = () => {
  const [selectedMonth, setSelectedMonth] = useState('');
  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (selectedMonth) {
      fetchChartData(selectedMonth);
    }
  }, [selectedMonth]);

  const fetchChartData = async (month) => {
    setLoading(true);
    try {
      const response = await fetch(`https://664c7d2a35bbda10988094cc.mockapi.io/task1/chart?month=${month}`);
      if (!response.ok) {
        throw new Error('Failed to fetch data');
      }
      const data = await response.json();
      setChartData(data);
      setLoading(false);
    } catch (error) {
      setError('Error fetching chart data');
      setLoading(false);
    }
  };

  const handleMonthChange = (event) => {
    setSelectedMonth(event.target.value);
  };

  return (
    <div>
      <select value={selectedMonth} onChange={handleMonthChange}>
        <option value="">Select a month</option>
        <option value="january">January</option>
        <option value="february">February</option>
        {/* Add more options for other months */}
      </select>

      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}

      {chartData && (
        <div>
          <h2>Chart Data for {selectedMonth}</h2>
          <p>Total Sale: {chartData.totalSale}</p>
          <p>Total Sold Items: {chartData.totalSoldItems}</p>
          <p>Total Not Sold Items: {chartData.totalNotSoldItems}</p>
          {/* Render your chart here using chartData */}
        </div>
      )}
    </div>
  );
};

export default ChartComponent;
